import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const Nav = () => {
  const { getCartCount } = useCart();

  return (
    <header className="nav">
      <div className="brand">
        <div className="brand-logo" aria-hidden="true"></div>
        <strong>TND Shop</strong>
        <span className="pill">TND Currency</span>
        <span className="pill">Cash on Delivery</span>
      </div>
      <nav className="nav-actions">
        <Link to="/" className="nav-btn">Shop</Link>
        <Link to="/cart" className="nav-btn">
          Cart <span className="pill">{getCartCount()}</span>
        </Link>
        <Link to="/admin/login" className="nav-btn">Admin</Link>
      </nav>
    </header>
  );
};

export default Nav;
